my $option = $ARGV[0];

my @candidate_vector_to_print;
my @candidate_vector;

if (($option =~ /stimulus/)) {
    #Create a stimulus
    my @reads = keys %read_hash;
    my $num_reads = $ARGV[1];
    my @test_vector;
    my @bin_vector;
    
    foreach $index (1 .. $num_reads) {
        #Pick out a random read and push into an array
        my $read = $reads[int(rand ($#reads + 1))];
        my $start_position = $read_hash{$read}->{"start_position"};
        my $end_position   = $read_hash{$read}->{"end_position"};
        my $quality_score  = $read_hash{$read}->{"quality_score"};
        my $read_length    = $read_hash{$read}->{"read_length"};
        my $txt_string     = "$read $start_position $end_position";
        my $bin_string     = &convertToBinary($read); $bin_string = sprintf "%0288b%s%08b%08b%08b%s", 0, $bin_string, $start_position, $end_position, $read_length, $quality_score;
        my @candidates     = keys $read_hash{$read}->{"candidates"};

        my @candidates_to_print = map {sprintf "%08b%0280b%s", $#candidates + 1, 0, &convertToBinary($_)} @candidates;
        my @candidates_complete = map {sprintf "%d,%s", $#candidates + 1, $_} @candidates;

        push @candidate_vector_to_print, @candidates_to_print;
        push @candidate_vector, @candidates_complete;
        push @bin_vector, "$bin_string";
        push @test_vector, "$txt_string";
    }
    
    $"="\n";
    open TXT, ">stimulus.txt";
    open BIN, ">stimulus.bin";
    open CAN_TXT, ">candidates.txt";
    open CAN_BIN, ">candidates.bin";
    print TXT "@test_vector";
    print BIN "@bin_vector";
    print CAN_TXT "@candidate_vector";
    print CAN_BIN "@candidate_vector_to_print";

    close CAN_BIN;
    close CAN_TXT;
    close BIN;
    close TXT;

    die;
}

my $log = $ARGV[1];
my $read_length = 112;

open LOG, "<$log";

my $prev_corrected_read = -1;
my $current_corrected_read;
my $num_candidates_for_corrected_read = 0;
my $num_candidates = -1;
my $correction_in_progress = "FALSE";

while ($line = <LOG>) {
    chomp($line);

    if ($line =~ /Read:([ACGT]+):([ACGT]+):([0-9]+)/) {
        #$current_corrected_read = `perl binaryToKmer.pl $1`;
        #my $candidate = `perl binaryToKmer.pl $2`;
        $current_corrected_read = $1;
        my $candidate = $2;
        my $num_cands = $3;
        chomp($current_corrected_read);
        chomp($candidate);
        chomp($num_cands);
        $current_corrected_read = substr $current_corrected_read, 0, $read_length;
        $candidate = substr $candidate, 0, $read_length;
        if ($correction_in_progress =~ /TRUE/) {
            print "ERROR! Number of candidates varies while read is being output!!!\n" if ($num_candidates != $num_cands + 0);
        }
        unless (exists $read_hash{$current_corrected_read}->{"candidates"}->{$candidate}) {
            print "ERROR! Candidate $candidate is not a candidate for correcting $current_corrected_read\n";
        }
        $num_candidates = $num_cands;
        $num_candidates = $num_candidates + 0;
        $num_candidates_for_corrected_read++;
        $correction_in_progress = "TRUE";
    } else {
        if ($line =~ /Completed printing candidates/) {
            print "Report for read: $current_corrected_read\n";
            my $num_total_candidates = $read_hash{$current_corrected_read}->{"num_candidates"};
            print "Number of total candidates : $num_total_candidates\n";
            print "Number of candidates output: $num_candidates_for_corrected_read\n";
            print "Number of candidates claimed by design: $num_candidates\n";

            if ($num_total_candidates > 28) {
                unless (($num_candidates_for_corrected_read == 28) && ($num_candidates == 28)) {
                    print "ERROR! Wrong number of candidates for $current_corrected_read\n";
                }
            } else {
                if ($num_candidates_for_corrected_read != $num_candidates) {
                    print "ERROR! Number of candidates output doesn't match projected number of candidates for $current_corrected_read\n";
                }
            }

            $num_candidates_for_corrected_read = 0;
            $correction_in_progress = "FALSE";
        }
    }
}

sub convertToBinary {
    my $kmer = $_[0];
    my @kmerSplit = reverse split //, $kmer;

    #Now convert to binary
    my $binary = "";
    foreach $k (@kmerSplit) {
        if ($k =~ /A/) {
            $binary .= "00";
        } 
        if ($k =~ /C/) {
            $binary .= "01";
        }
        if ($k =~ /G/) {
            $binary .= "10";
        }
        if ($k =~ /T/) {
            $binary .= "11";
        }
    }
    return "$binary";
}
